mapper.update:
UPDATE community_post
title = #{title}, content = #{content}, category = #{category}, view_count = #{viewCount}, like_count = #{likeCount}, comment_count = #{commentCount}, collect_count = #{collectCount}, is_top = #{isTop}, top_expire_time = #{topExpireTime}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM community_post


        AND status = #{status}


        AND author_name LIKE CONCAT('%', #{authorName}, '%')


        AND category = #{category}


        AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}

SELECT 
FROM community_post


        AND status = #{request.status}


        AND author_name LIKE CONCAT('%', #{request.authorName}, '%')


        AND category = #{request.category}


        AND (title LIKE CONCAT('%', #{request.keyword}, '%') OR content LIKE CONCAT('%', #{request.keyword}, '%'))


        AND create_time >= #{request.startTime}


        AND create_time <= #{request.endTime}


ORDER BY is_top DESC, create_time DESC
LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE community_post 
SET is_top = 1, top_expire_time = #{topExpireTime}, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET is_top = 0, top_expire_time = NULL, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET status = 2, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET status = 3, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET view_count = view_count + 1, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET like_count = like_count + #{count}, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET collect_count = collect_count + #{count}, update_time = NOW()
WHERE id = #{id}

UPDATE community_post 
SET comment_count = comment_count + #{count}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT FROM community_post
AND status = 1
AND category = #{request.category}
AND (title LIKE CONCAT('%', #{request.keyword}, '%') OR content LIKE CONCAT('%', #{request.keyword}, '%'))
ORDER BY is_top DESC, create_time DESC LIMIT #{offset}, #{pageSize}
