mapper.insert:
INSERT INTO interview_favorite (
    user_id, target_type, target_id, create_time
) VALUES (
    #{userId}, #{targetType}, #{targetId}, #{createTime}
)

mapper.delete:
DELETE FROM interview_favorite 
WHERE user_id = #{userId} 
AND target_type = #{targetType} 
AND target_id = #{targetId}

DELETE FROM interview_favorite 
WHERE target_type = #{targetType} 
AND target_id = #{targetId}

mapper.select:
SELECT COUNT(*) > 0
FROM interview_favorite
WHERE user_id = #{userId} 
AND target_type = #{targetType} 
AND target_id = #{targetId}

SELECT COUNT(*)
FROM interview_favorite
WHERE target_type = #{targetType} 
AND target_id = #{targetId}

mapper.delete:
